function deletePost(n){const t=$("#blog_asp_page").val();$.ajax({type:"POST",url:`/Blog/${t}?handler=DeletePost`,data:{postId:n},beforeSend:function(n){n.setRequestHeader("XSRF-TOKEN",$('input:hidden[name="__RequestVerificationToken"]').val())}}).done(function(n){$("#blog_directory_container").html(n);directoryTop()})}function directoryTop(){const n=document.getElementById("directoryTop").offsetTop;window.scrollTo(0,n)}function filterDirectory(n){const t=$("#blog_asp_page").val();$.ajax({type:"POST",url:`/Blog/${t}?handler=FilterDirectory`,data:{category:n},beforeSend:function(n){n.setRequestHeader("XSRF-TOKEN",$('input:hidden[name="__RequestVerificationToken"]').val())}}).done(function(n){$("#blog_directory_container").html(n);directoryTop()})}function showNextDirectoryPage(){const n=$("#blog_asp_page").val();$.ajax({type:"POST",url:`/Blog/${n}?handler=ShowDirectoryNextPage`,beforeSend:function(n){n.setRequestHeader("XSRF-TOKEN",$('input:hidden[name="__RequestVerificationToken"]').val())}}).done(function(n){$("#blog_directory_container").html(n);directoryTop()})}function showPreviousDirectoryPage(){const n=$("#blog_asp_page").val();$.ajax({type:"POST",url:`/Blog/${n}?handler=ShowDirectoryPreviousPage`,beforeSend:function(n){n.setRequestHeader("XSRF-TOKEN",$('input:hidden[name="__RequestVerificationToken"]').val())}}).done(function(n){$("#blog_directory_container").html(n);directoryTop()})}$(document).ready(function(){$("#blog_directory_next_href").on("click",function(){showNextDirectoryPage()});$("#blog_directory_previous_href").on("click",function(){showPreviousDirectoryPage()});$(".category-link").on("click",function(){const n=$(this).data("category");filterDirectory(n)});$(".delete-post").on("click",function(){const n=$(this).data("post-id"),t=$(this).data("post-title"),i=`Are you sure you wish to delete "${t}"?`;confirm(i)&&deletePost(n)})});